<?php /*------------------------------Menu Name: TestFile Name: C_test.phpFile Path: /var/www/apn/application/controllers/C_test.phpCreate Date Time: 2022-01-05 10:46:39------------------------------*/defined('BASEPATH') OR exit('No direct script access allowed');class c_test extends CI_Controller { 	public function __construct() 	{ 		parent::__construct(); 		$a = new libSession(); 		if($a->gf_check_session()) 			redirect("c_core_login"); 		else 		{ 			$this->load->model(array('m_core_user_menu', 'm_core_user_login', 'm_core_apps', 'm_test')); 			$this->load->library(array('libPaging')); 		}		} 	public function index() 	{ 		$data['o_page'] = 'backend/v_test'; 		$data['o_page_title'] = 'Test'; 		$data['o_page_desc'] = 'Maintenance Test'; 		$data['o_data'] = null; 		$data['o_mode'] = "I"; 		$data['o_side_bar'] = $this->m_core_user_menu->gf_recursive_side_bar(array("nMenuId" => 0, "nMenuIdInit" => 0));		$data['o_info'] = $this->m_core_user_login->gf_user_info();		$data['o_config'] = $this->m_core_apps->gf_read_config_apps();		$oAuth = json_decode($this->m_core_user_login->gf_load_auth(array("sMenuCtlName" => "c_test")), TRUE); 		$data['o_save']   = trim($oAuth['sSave']); 		$data['o_update'] = trim($oAuth['sUpdate']); 		$data['o_delete'] = trim($oAuth['sDelete']); 		$data['o_cancel'] = trim($oAuth['sCancel']); 		$this->load->view('backend/v_core_main', $data); 	} 	public function gf_exec() 	{ 		$data['o_page'] = 'backend/v_test'; 		$data['o_page_title'] = 'Test'; 		$data['o_page_desc'] = 'Maintenance Test'; 		$data['o_data'] = null; 		$data['o_mode'] = ""; 		$data['o_data'] = $this->m_test->gf_load_data(); 		$data['o_side_bar'] = $this->m_core_user_menu->gf_recursive_side_bar(array("nMenuId" => 0, "nMenuIdInit" => 0));		$data['o_info'] = $this->m_core_user_login->gf_user_info();		$data['o_config'] = $this->m_core_apps->gf_read_config_apps();		$oAuth = json_decode($this->m_core_user_login->gf_load_auth(array("sMenuCtlName" => "c_test")), TRUE); 		$data['o_save']   = trim($oAuth['sSave']); 		$data['o_update'] = trim($oAuth['sUpdate']); 		$data['o_delete'] = trim($oAuth['sDelete']); 		$data['o_cancel'] = trim($oAuth['sCancel']); 		$this->load->view('backend/v_core_main', $data); 	} 	function gf_transact() 	{ 		print $this->m_test->gf_transact(); 	} 	function gf_load_data() 	{ 		$c = new libPaging(); 		$sParam = array( "sSQL" => "select '' as Column1, '' as Column2, '' as Column3, '' as Column4, '' as Column5, '' as Column6, '' as Column7",										 "sTitleHeader" => "Title Sample",										 "sCallBackURLPaging"	 => site_url()."c_test/gf_load_data",										 "sCallBackURLPageEditDelete" => site_url()."c_test/gf_exec",										 "sLookupEditDelete" => true,										 "bDebugSQL" => false,										 "sLayout" => ($this->m_core_apps->gf_is_mobile() ? "COL_SYSTEM" : "GRID_SYSTEM"),										 "sInitHeaderFields" => array("Column1"),										 "sDefaultFieldSearch"  => "Column1",										 "sTheme" => "default"); 		$p = $c->gf_render_paging_data($sParam); 		print $p; 	} }