<?php /*------------------------------Menu Name: Status DokumenFile Name: M_apn_status_dokumen.phpFile Path: D:\Project\PHP\pis\application\models\M_apn_status_dokumen.phpCreate Date Time: 2020-05-22 12:45:53------------------------------*/class m_apn_status_dokumen extends CI_Model { 	public function __construct() 	{ 		parent::__construct(); 		$this->load->model(array('m_core_apps')); 	} 	function gf_load_data($sParam=null) 	{ 		$oParam = $_POST; 		$oData = $sParam === null ? $oParam['Id_Status_Dokumen'] : $sParam['Id_Status_Dokumen']; 		$sql = "call sp_query('select nIdStatusDokumen, sNamaStatusDokumen from tm_apn_status_dokumen where sStatusDelete is null and nUnitId_fk = ".$this->session->userdata('nUnitId_fk')." and nIdStatusDokumen = ''".trim($oData)."'' ')"; 		$rs = $this->db->query($sql); 		return $rs->result_array(); 	} 	function gf_transact() 	{ 		$hideMode 								= $this->input->post('hideMode', TRUE); 		$txtIdStatusDokumen 			= $this->input->post('txtIdStatusDokumen', TRUE); 		$txtNamaStatusDokumen 		= $this->input->post('txtNamaStatusDokumen', TRUE); 		$txtNamaStatusDokumenOld 	= $this->input->post('txtNamaStatusDokumenOld', TRUE); 		$sReturn = null; 		$UUID = "NULL"; 		if(trim($hideMode) !== "I") 			$UUID = trim($txtIdStatusDokumen); 		if(in_array(trim($hideMode), array("I", "U"))) 		{ 			if(strtolower(trim($txtNamaStatusDokumen)) !== strtolower(trim($txtNamaStatusDokumenOld))) 			{ 				$sRet = json_decode($this->m_core_apps->gf_check_double_data_in_table(array("sTableName" => "tm_apn_status_dokumen", "sFieldName" => "sNamaStatusDokumen", "sContent" => trim($txtNamaStatusDokumen))), TRUE); 				if(intVal($sRet['status']) === 1) 				{ 					$sReturn = json_encode(array("status" => -1, "message" => trim($sRet['message']))); 					return $sReturn; 					exit(0); 				} 			} 		} 		if(in_array(trim($hideMode), array("D"))) 		{ 			$sRet = json_decode($this->m_core_apps->gf_check_foreign_key_use(array("sDatabaseName" => trim($this->db->database), "sFieldName" => "nIdStatusDokumen_fk", "sContent" => trim($txtIdStatusDokumen), "sValueLabel" => "Id StatusDokumen")), TRUE); 			if(intVal($sRet['status']) === 1) 			{ 				$sReturn = json_encode(array("status" => -1, "message" => trim($sRet['message']))); 				return $sReturn; 				exit(0); 			} 		} 		$sql = "call sp_tm_apn_status_dokumen('".trim($hideMode)."', ".trim($UUID).", '".trim($txtNamaStatusDokumen)."', NULL, ".$this->session->userdata('nUnitId_fk').", '".trim($this->session->userdata('sRealName'))."'); "; 		$this->db->trans_begin(); 		$this->db->query($sql); 		if ($this->db->trans_status() === FALSE) 		{ 			$this->db->trans_rollback(); 			$sReturn = json_encode(array("status" => -1, "message" => "Failed")); 		} 		else 		{ 			$this->db->trans_commit(); 			$sReturn = json_encode(array("status" => 1, "message" => "Data has been Submitted.")); 		} 		return $sReturn; 	} }