<?php class m_prepayment_departemen extends CI_Model { 	public function __construct() 	{ 		parent::__construct(); 		$this->load->model(array('m_core_apps')); 	} 	function gf_load_data($sParam=null) 	{ 		$oParam = $_POST; 		$oData = $sParam === null ? $oParam['Id_Departemen'] : $sParam['Id_Departemen']; 		$sql = "call sp_query('select nIdDepartemen, sNamaDepartemen from tm_prepayment_departemen where sStatusDelete is null and nUnitId_fk = ".$this->session->userdata('nUnitId_fk')." and nIdDepartemen = ".trim($oData)."')"; 		$rs = $this->db->query($sql); 		return $rs->result_array(); 	} 	function gf_transact() 	{ 		$txtIdDepartemen = $this->input->post('txtIdDepartemen', TRUE); 		$txtNamaDepartemen = $this->input->post('txtNamaDepartemen', TRUE); 		$txtNamaDepartemenOld = $this->input->post('txtNamaDepartemenOld', TRUE); 		$hideMode = $this->input->post('hideMode', TRUE); 		$sReturn = null; 		$UUID = "NULL"; 		if(trim($hideMode) !== "I") 			$UUID = trim($txtIdDepartemen); 		if(in_array(trim($hideMode), array("I", "U"))) 		{ 			if(strtolower(trim($txtNamaDepartemen)) !== strtolower(trim($txtNamaDepartemenOld))) 			{ 				$sRet = json_decode($this->m_core_apps->gf_check_double_data_in_table(array("sTableName" => "tm_prepayment_departemen", "sFieldName" => "sNamaDepartemen", "sContent" => trim($txtNamaDepartemen))), TRUE); 				if(intVal($sRet['status']) === 1) 				{ 					$sReturn = json_encode(array("status" => -1, "message" => trim($sRet['message']))); 					return $sReturn; 					exit(0); 				} 			} 		} 		if(in_array(trim($hideMode), array("D"))) 		{ 			$sRet = json_decode($this->m_core_apps->gf_check_foreign_key_use(array("sDatabaseName" => trim($this->db->database), "sFieldName" => "nIdDepartemen_fk", "sContent" => trim($txtIdDepartemen), "sValueLabel" => "Id Departemen")), TRUE); 			if(intVal($sRet['status']) === 1) 			{ 				$sReturn = json_encode(array("status" => -1, "message" => trim($sRet['message']))); 				return $sRetur