<?php class m_prepayment_divisi extends CI_Model { 	public function __construct() 	{ 		parent::__construct(); 		$this->load->model(array('m_core_apps')); 	} 	function gf_load_data($sParam=null) 	{ 		$oParam = $_POST; 		$oData = $sParam === null ? $oParam['Id_Divisi'] : $sParam['Id_Divisi']; 		$sql = "call sp_query('select nIdDivisi, sNamaDivisi from tm_prepayment_divisi where sStatusDelete is null and nUnitId_fk = ".$this->session->userdata('nUnitId_fk')." and nIdDivisi = ".trim($oData)."')"; 		$rs = $this->db->query($sql); 		return $rs->result_array(); 	} 	function gf_transact() 	{ 		$txtIdDivisi = $this->input->post('txtIdDivisi', TRUE); 		$txtNamaDivisi = $this->input->post('txtNamaDivisi', TRUE); 		$txtNamaDivisiOld = $this->input->post('txtNamaDivisiOld', TRUE); 		$hideMode = $this->input->post('hideMode', TRUE); 		$sReturn = null; 		$UUID = "NULL"; 		if(trim($hideMode) !== "I") 			$UUID = trim($txtIdDivisi); 		if(in_array(trim($hideMode), array("I", "U"))) 		{ 			if(strtolower(trim($txtNamaDivisi)) !== strtolower(trim($txtNamaDivisiOld))) 			{ 				$sRet = json_decode($this->m_core_apps->gf_check_double_data_in_table(array("sTableName" => "tm_prepayment_divisi", "sFieldName" => "sNamaDivisi", "sContent" => trim($txtNamaDivisi))), TRUE); 				if(intVal($sRet['status']) === 1) 				{ 					$sReturn = json_encode(array("status" => -1, "message" => trim($sRet['message']))); 					return $sReturn; 					exit(0); 				} 			}		} 		if(in_array(trim($hideMode), array("D"))) 		{ 			$sRet = json_decode($this->m_core_apps->gf_check_foreign_key_use(array("sDatabaseName" => trim($this->db->database), "sFieldName" => "nIdDivisi_fk", "sContent" => trim($txtIdDivisi), "sValueLabel" => "Id Divisi")), TRUE); 			if(intVal($sRet['status']) === 1) 			{ 				$sReturn = json_encode(array("status" => -1, "message" => trim($sRet['message']))); 				return $sReturn; 				exit(0); 			} 		} 		$sql = "call sp_tm_prepayment_divisi('".$hideMode."', ".trim($UUID).", '".trim($txtNamaDivisi)."', NULL, ".$this->session->userdata('nUnitId_fk').", '".trim($this->session->userdata('sRealName'))."')"; 		$this->db->trans_begin(); 		$this->db->query($sql); 		if ($this->db->trans_status() === FALSE) 		{ 			$this->db->trans_rollback(); 			$sReturn = json_encode(array("status" => -1, "message" => "Failed")); 		} 		else 		{ 			$this->db->trans_commit(); 			$sReturn = json_encode(array("status" => 1, "message" => "Data has been Submitted.")); 		} 		return $sReturn; 	} }