<?php class m_prepayment_kategori_trx extends CI_Model { 	public function __construct() 	{ 		parent::__construct(); 		$this->load->model(array('m_core_apps')); 	} 	function gf_load_data($sParam=null) 	{ 		$oParam = $_POST; 		$oData = $sParam === null ? $oParam['Id_Kategori_TRX'] : $sParam['Id_Kategori_TRX']; 		$sql = "call sp_query('select nIdKategoriTRX, sNamaKategoriTRX from tm_prepayment_kategori_trx where sStatusDelete is null and nUnitId_fk = ".$this->session->userdata('nUnitId_fk')." and nIdKategoriTRX = ".trim($oData)."')"; 		$rs = $this->db->query($sql); 		return $rs->result_array(); 	} 	function gf_transact() 	{ 		$txtIdKategoriTRX = $this->input->post('txtIdKategoriTRX', TRUE); 		$txtNamaKategoriTRX = $this->input->post('txtNamaKategoriTRX', TRUE); 		$txtNamaKategoriTRXOld = $this->input->post('txtNamaKategoriTRXOld', TRUE); 		$hideMode = $this->input->post('hideMode', TRUE); 		$sReturn = null; 		$UUID = "NULL"; 		if(trim($hideMode) !== "I") 			$UUID = trim($txtIdKategoriTRX); 		if(in_array(trim($hideMode), array("I", "U"))) 		{ 			if(strtolower(trim($txtNamaKategoriTRX)) !== strtolower(trim($txtNamaKategoriTRXOld))) 			{ 				$sRet = json_decode($this->m_core_apps->gf_check_double_data_in_table(array("sTableName" => "tm_prepayment_kategori_trx", "sFieldName" => "sNamaKategoriTRX", "sContent" => trim($txtNamaKategoriTRX))), TRUE); 				if(intVal($sRet['status']) === 1) 				{ 					$sReturn = json_encode(array("status" => -1, "message" => trim($sRet['message']))); 					return $sReturn; 					exit(0); 				} 			} 		} 		if(in_array(trim($hideMode), array("D"))) 		{ 			$sRet = json_decode($this->m_core_apps->gf_check_foreign_key_use(array("sDatabaseName" => "billingdb", "sFieldName" => "nIdKategoriTRX_fk", "sContent" => trim($txtIdKategoriTRX), "sValueLabel" => "Id KategoriTRX")), TRUE); 			if(intVal($sRet['status']) === 1) 			{ 				$sReturn = json_encode(array("status" => -1, "message" => trim($sRet['message']))); 				return $sReturn; 				exit(0); 			} 		} 		$sql = "call sp_tm_prepayment_kategori_trx('".$hideMode."', ".trim($UUID).", '".trim($txtNamaKategoriTRX)."', NULL, ".$this->session->userdata('nUnitId_fk').", '".trim($this->session->userdata('sRealName'))."')"; 		$this->db->trans_begin(); 		$this->db->query($sql); 		if ($this->db->trans_status() === FALSE) 		{ 			$this->db->trans_rollback(); 			$sReturn = json_encode(array("status" => -1, "message" => "Failed")); 		} 		else 		{ 			$this->db->trans_commit(); 			$sReturn = json_encode(array("status" => 1, "message" => "Data has been Submitted.")); 		} 		return $sReturn; 	} }