<?php class m_prepayment_unit_usaha extends CI_Model { 	public function __construct() 	{ 		parent::__construct(); 		$this->load->model(array('m_core_apps')); 	} 	function gf_load_data($sParam=null) 	{ 		$oParam = $_POST; 		$oData = $sParam === null ? $oParam['Nama_Unit_Usaha'] : $sParam['Nama_Unit_Usaha']; 		$sql = "call sp_query('select nIdUnitUsaha, sNamaUnitUsaha from tm_prepayment_unit_usaha where sStatusDelete is null and nUnitId_fk = ".$this->session->userdata('nUnitId_fk')." and sNamaUnitUsaha = ''".trim($oData)."'' ')"; 		$rs = $this->db->query($sql); 		return $rs->result_array(); 	} 	function gf_transact() 	{ 		$txtIdUnitUsaha = $this->input->post('txtIdUnitUsaha', TRUE); 		$txtNamaUnitUsaha = $this->input->post('txtNamaUnitUsaha', TRUE); 		$txtNamaUnitUsahaOld = $this->input->post('txtNamaUnitUsahaOld', TRUE); 		$hideMode = $this->input->post('hideMode', TRUE); 		$sReturn = null; 		$UUID = "NULL"; 		if(trim($hideMode) !== "I") 			$UUID = trim($txtIdUnitUsaha); 		if(in_array(trim($hideMode), array("I", "U"))) 		{ 			if(strtolower(trim($txtNamaUnitUsaha)) !== strtolower(trim($txtNamaUnitUsahaOld))) 			{ 				$sRet = json_decode($this->m_core_apps->gf_check_double_data_in_table(array("sTableName" => "tm_prepayment_unit_usaha", "sFieldName" => "sNamaUnitUsaha", "sContent" => trim($txtNamaUnitUsaha))), TRUE); 				if(intVal($sRet['status']) === 1) 				{ 					$sReturn = json_encode(array("status" => -1, "message" => trim($sRet['message']))); 					return $sReturn; 					exit(0); 				} 			} 		} 		if(in_array(trim($hideMode), array("D"))) 		{ 			$sRet = json_decode($this->m_core_apps->gf_check_foreign_key_use(array("sDatabaseName" => trim($this->db->database), "sFieldName" => "nIdUnitUsaha_fk", "sContent" => trim($txtIdUnitUsaha), "sValueLabel" => "Id Unit Usaha")), TRUE); 			if(intVal($sRet['status']) === 1) 			{ 				$sReturn = json_encode(array("status" => -1, "message" => trim($sRet['message']))); 				return $sReturn; 				exit(0); 			} 		} 		$sql = "call sp_tm_prepayment_unit_usaha('".$hideMode."', ".$UUID.", '".trim($txtNamaUnitUsaha)."', NULL, ".$this->session->userdata('nUnitId_fk').", '".trim($this->session->userdata('sRealName'))."')"; 		$this->db->trans_begin(); 		$this->db->query($sql); 		if ($this->db->trans_status() === FALSE) 		{ 			$this->db->trans_rollback(); 			$sReturn = json_encode(array("status" => -1, "message" => "Failed")); 		} 		else 		{ 			$this->db->trans_commit(); 			$sReturn = json_encode(array("status" => 1, "message" => "Data has been Submitted.")); 		} 		return $sReturn; 	} }