<?php /*------------------------------Menu Name: TestFile Name: M_test.phpFile Path: /var/www/apn/application/models/M_test.phpCreate Date Time: 2022-01-05 10:46:39------------------------------*/class m_test extends CI_Model { 	public function __construct() 	{ 		parent::__construct(); 		$this->load->model(array('m_core_apps')); 	} 	function gf_load_data($sParam=null) 	{ 		$oParam = $_POST; 		$oData = $sParam === null ? $oParam['key_value'] : $sParam['key_value']; 		$sql = "call sp_query('select * from [table_name] where sStatusDelete is null and nUnitId_fk = 1 and and [primary_key] = ".trim($oData)."')"; 		$rs = $this->db->query($sql); 		return $rs->result_array(); 	} 	function gf_transact() 	{ 		$hideMode = $this->input->post('hideMode', TRUE); 		$txt1 = $this->input->post('txt1', TRUE); 		$txt2 = $this->input->post('txt2', TRUE); 		$txt3 = $this->input->post('txt3', TRUE); 		$txt4 = $this->input->post('txt4', TRUE); 		$txt5 = $this->input->post('txt5', TRUE); 		$txt6 = $this->input->post('txt6', TRUE); 		$sReturn = null; 		$UUID = "NULL"; 		if(trim($hideMode) !== "I") 			$UUID = trim($txt1); 		if(in_array(trim($hideMode), array("I", "U"))) 		{ 			if(strtolower(trim($nama_object_perbandingan_baru)) !== strtolower(trim($nama_object_perbandingan_lama))) 			{ 				$sRet = json_decode($this->m_core_apps->gf_check_double_data_in_table(array("sTableName" => "nama_table", "sFieldName" => "nama_field", "sContent" => "nilai_object_perbandingan")), TRUE); 				if(intVal($sRet['status']) === 1) 				{ 					$sReturn = json_encode(array("status" => -1, "message" => trim($sRet['message']))); 					return $sReturn; 					exit(0); 				} 			} 		} 		if(in_array(trim($hideMode), array("D"))) 		{ 			$sRet = json_decode($this->m_core_apps->gf_check_foreign_key_use(array("sDatabaseName" => trim($this->db->database), "sFieldName" => "nama_field", "sContent" => "nilai_object_perbandingan", "sValueLabel" => "label_object_warning_ke_client")), TRUE); 			if(intVal($sRet['status']) === 1) 			{ 				$sReturn = json_encode(array("status" => -1, "message" => trim($sRet['message']))); 				return $sReturn; 				exit(0); 			} 		} 		$sql = "call sp_(); "; 		$this->db->trans_begin(); 		$this->db->query($sql); 		if ($this->db->trans_status() === FALSE) 		{ 			$this->db->trans_rollback(); 			$sReturn = json_encode(array("status" => -1, "message" => "Failed")); 		} 		else 		{ 			$this->db->trans_commit(); 			$sReturn = json_encode(array("status" => 1, "message" => "Data has been Submitted.")); 		} 		return $sReturn; 	} }