<?php $oTab1 = ""; $oTab2 = ""; $oButton = ""; if(trim($o_mode) === "I") { $oTab1 = "active"; $oButton = $o_save; } else { $oTab2 = "active"; $oButton = $o_update." ".$o_delete;} $oButton .= " ".$o_cancel; ?> <div class="nav-tabs-custom"> 	<ul class="nav nav-tabs"> 		<li class="<?php print $oTab1; ?>"><a data-toggle="tab" href="#tab_1">List Status Dokumen</a></li> 		<li class="<?php print $oTab2; ?>"><a data-toggle="tab" href="#tab_2">Form Status Dokumen</a></li> 	</ul> 	<div class="tab-content"> 		<div id="tab_1" class="tab-pane <?php print $oTab1; ?>"> 			<div id="div-list-user">				</div> 		</div> 		<!-- /.tab-pane --> 		<div id="tab_2" class="tab-pane <?php print $oTab2; ?>"> 			<form id="form_5ca2dcddb1a39" role="form" action="<?php print site_url(); ?>c_prepayment_status/gf_transact" method="post"> 				<div class="box-body no-padding"> 				<div class="row">					<div class="col-sm-4 col-xs-12 col-md-4 col-lg-2 form-group" id="div-top"><label>Id Status Dokumen</label><input allow-empty="false" type="text" placeholder="Id Status Dokumen" name="txtIdStatus" id="txtIdStatus" class="form-control" maxlength="50" value="<?php print trim($o_mode) === "I" ? "(AUTO)" : trim($o_data[0]['nIdStatus']); ?>" readonly>					</div>				  <div class="row"></div>					<div class="col-sm-12 col-xs-12 col-md-6 col-lg-6 form-group"><label>Nama Status Dokumen</label><input allow-empty="false" type="text" placeholder="Nama Status Dokumen" name="txtNamaStatus" id="txtNamaStatus" class="form-control" maxlength="100" value="<?php print trim($o_data[0]['sNamaStatus']); ?>">						<input type="hidden" name="txtNamaStatusOld" id="txtNamaStatusOld" value="<?php print trim($o_data[0]['sNamaStatus']); ?>" />				  </div>				</div> 				<div class="box-footer no-padding"> 					<br /> 					<?php print $oButton; ?> 				</div> 				<input type="hidden" name="hideMode" id="hideMode" value="" /> 			</form> 		</div> 	</div> <!-- /.tab-content --> </div> <script> var sSURL  = "<?php print site_url(); ?>c_prepayment_status/gf_upload/" var sParam = ""; var dialog = ""; $(function() { 	gf_load_data(); 	$("input[content-mode='numeric']").autoNumeric('init', {mDec: '0'}); 	$("button[id='button-submit']").click(function() 	{ 		if($.trim($(this).html()) === "Cancel") 			window.location.href = "<?php print site_url(); ?>c_prepayment_status" 		else 		{ 			var bNext = true; 			var objForm = $("#form_5ca2dcddb1a39"); 			//------------------------------------------ 			if($.trim($(this).html()) === "Save") 				$("#hideMode").val("I"); 			else if($.trim($(this).html()) === "Update") 				$("#hideMode").val("U"); 			else if($.trim($(this).html()) === "Delete") 				$("#hideMode").val("D"); 			//------------------------------------------ 			if(parseInt($.inArray($.trim($("#hideMode").val()), ["I", "U"])) !== -1) 			{ 				var oRet = $.gf_valid_form({"oForm": objForm, "oAddMarginLR": true, oObjDivAlert: $("#div-top")}); 				bNext = oRet.oReturnValue; 				} 			if(!bNext) 				return false; 			//------------------------------------------ 			$.gf_custom_ajax({"oForm": objForm,  			"success": function(r)			{				var JSON = $.parseJSON(r.oRespond); 				if(JSON.status === 1) 					window.location.href = "<?php print site_url(); ?>c_prepayment_status/";				else 				{ 					$.gf_remove_all_modal(); 					r.oDialog.open(); 					r.oDialog.getModalBody().html(JSON.message); 					r.oDialog.setType(BootstrapDialog.TYPE_DEFAULT); 					r.oDialog.getModalFooter().find("button[id='cmd-loading']").removeClass("disabled").html("Close").unbind("click").bind("click", function() 					{ 						r.oDialog.close();  					});					} 			}, 			"validate": true,			"beforeSend": function(r) {},			"beforeSendType": "standard", 			"error": function(r) {} 			}); 		} 	}); }); function gf_load_data() { 	$("#div-list-user").ado_load_paging_data({url: "<?php print site_url(); ?>c_prepayment_status/gf_load_data/"}); } </script>