 				<input type="hidden" name="hideMode" id="hideMode" value="" /> 			</form> 		</div> 	</div> </div> <script> var sSURL  = "<?php print site_url(); ?>c_test/gf_upload/" var sParam = ""; var dialog = ""; $(function() { 	gf_load_data(); 	$("input[content-mode='numeric']").autoNumeric('init', {mDec: '0'}); 	$("button[id='button-submit']").click(function() 	{ 		if($.trim($(this).html()) === "Cancel") 			window.location.href = "<?php print site_url(); ?>c_test" 		else 		{ 			var bNext = true; 			var objForm = $("#form_61d514c727366"); 			//------------------------------------------ 			if($.trim($(this).html()) === "Save") 				$("#hideMode").val("I"); 			else if($.trim($(this).html()) === "Update") 				$("#hideMode").val("U"); 			else if($.trim($(this).html()) === "Delete") 				$("#hideMode").val("D"); 			//------------------------------------------ 			if(parseInt($.inArray($.trim($("#hideMode").val()), ["I", "U"])) !== -1) 			{ 				var oRet = $.gf_valid_form({"oForm": objForm, "oAddMarginLR": true, oObjDivAlert: $("#div-top")}); 				bNext = oRet.oReturnValue; 				} 			if(!bNext) 				return false; 			//------------------------------------------ 			$.gf_custom_ajax({"oForm": objForm,  			"success": function(r)			{				var JSON = $.parseJSON(r.oRespond); 				if(JSON.status === 1) 					window.location.href = "<?php print site_url(); ?>c_test/";				else 				{ 					$.gf_clear_blur(); 					$.gf_remove_all_modal(); 					objForm.css({"-webkit-filter": "blur(0px)", "filter": "blur(0px)"});					$.gf_msg_info({oObjDivAlert: $("#div-top"), oAddMarginLR: true, oMessage: JSON.message});				} 			}, 			"validate": true,			"beforeSend": function(r) {},			"beforeSendType": "standard", 			"error": function(r) {} 			}); 		} 	}); }); function gf_load_data() { 	$("#div-list-user").ado_load_paging_data({url: "<?php print site_url(); ?>c_test/gf_load_data/"}); } </script>